	<link rel="stylesheet" href="/resource/css/slick.css">
	<script src="/resource/js/slick.min.js"></script>
	<script src="/resource/js/layui/lay/dest/layui.all.js"></script>
	<script>
		$(document).ready(function() {
			$('.m-pic2-qm .slider').slick({
				dots: false,
				arrows: true,
				autoplay: true,
				slidesToShow: 1,
				autoplaySpeed: 5000,
				pauseOnHover: false,
				lazyLoad: 'ondemand'
			});
		});
	</script>
	<script type="text/javascript">
		$(document).ready(function() {

		});

		layui.upload({
			url: "/index.php?m=api&c=index&v=uploadpic",
			type: 'image',
			ext: 'jpg|png|jpeg|bmp',
			before: function(obj) {
				$('#picslist').before('<span style="color: #d71618;" class="tips">上传中...</span>');
			},
			success: function(data) {
				var msg = data.msg;
				if(msg !== undefined) {
					layer.msg(msg);
				}
				$('#piclist').html('<div class="upic"><img src="' + data.url + '" class="layui-upload-img"></div>');

			}
		});
		layui.use('layedit', function() {
			var layedit = layui.layedit,
				$ = layui.jquery;
			layedit.set({
				uploadImage: {
					url: '/index.php?m=api&c=index&v=lay_uploadpic', //接口url
					type: 'post' //默认post
				}
			});
			//构建一个默认的编辑器
			var index = layedit.build('LAY_demo1');

			//编辑器外部操作
			var active = {
				content: function() {
					var content = layedit.getContent(index);
					add(content);
				},
				text: function() {
					var content = layedit.getContent(index);
					draft(content); //获取编辑器纯文本内容
				},
				selection: function() {
					alert(layedit.getSelection(index));
				}
			};

			$('.site-demo-layedit').on('click', function() {
				var type = $(this).data('type');
				active[type] ? active[type].call(this) : '';
			});
		});
		function add (con) {
			var title = $('#title').val();
			var desc = $('#describe').val();
			var did = $('#did').val();
			var imgUrl = $('.layui-upload-img').attr('src');
			if(!title) {
				layer.msg('请输入标题');
				return false;
			}
			if(!con) {
				layer.msg('请输入正文');
				return false;
			}
			if(!desc) {
				layer.msg('请输入描述');
				return false;
			}
			
			if(!imgUrl) {
				layer.msg('请上传图片');
				return false;
			}
			if(!$("input[type='checkbox']").is(':checked')) {
				layer.msg('请选择服务协议');
				return false;
			}
			$.post("index.php?m=api&c=Travelnote&v=save_travel_note", {
				'title': title,
				'content': con,
				'did':did,
				'imgUrl': imgUrl,
				'desc': desc
			}, function(data) {
				layer.msg(data.tips);
				if(data.status == 1) {
					window.location.href = "index.php?m=index&c=user&v=travel";
				}
			}, "JSON");
		}
		
		//保存草稿
		function draft(con) {
			var title = $('#title').val();
			var describe = $('#describe').val();
			var imgUrl = $('.layui-upload-img').attr('src');
			var list = imgUrl + '||' + con;
			$.post("/index.php?m=api&c=index&v=adddraft", {
				'title': title,
				'list': list,
				'type': 2,
				'describe': describe
			}, function(data) {
				layer.msg(data.tips);
				if(data.status == 1) {
					window.location.href = "index.php?m=index&c=user&v=draft";
				}
			}, "JSON");

		}
	</script>
</body>
</html>